        public static void SendEmailChangeRequest(string token, string recipient)
        {
            try
            {
                string body = @"<html><head><title>Αλλαγή e-mail στο Energy Room App</title></head><body><table border='0' width='60%' cellpadding='10'>";
                body += string.Format(@"data:image/jpeg;base64,");
                body += string.Format(@"<p>Ζήτησες αλλαγή του e-mail σου για την εφαρμογή Energy Room;</p><br>Αν ναι, τότε ολοκλήρωσε τη διαδικασία κάνοντας κλικ εδώ:<br>" + token);
                body += @"</body></html>";

                MailMessage mail = new MailMessage();
                SmtpClient SmtpServer = new SmtpClient("ldalipis.gr");

                mail.From = new MailAddress("info@ldalipis.gr");
                mail.To.Add(recipient);
                mail.Subject = "Αλλαγή e-mail στο Energy Room App";
                mail.Body = body;
                mail.IsBodyHtml = true;

                SmtpServer.Port = 25;
                SmtpServer.Host = "ldalipis.gr";
                SmtpServer.EnableSsl = true;
                SmtpServer.UseDefaultCredentials = false;
                SmtpServer.Credentials = new System.Net.NetworkCredential("info@ldalipis.gr", "Opeth1986!Banshee");

                SmtpServer.Send(mail);
            }
            catch (Exception ex)
            {
                // ex.Message
            }
        }

